import React, {Component} from 'react';
import {Map, TileLayer, Marker, Popup} from 'react-leaflet';
import '../App.css';
import L from 'leaflet';

class MyMap extends Component{
    constructor(props){
        super(props)
        this.state = {
            lat: this.props.lat,
            lng: this.props.long,
            zoom: 2,
            venues: []
        }
    }

    componentDidMount(){
        this.findVenueLocations();
    }

    // componentWillReceiveProps(nextProps){
    //     if(nextProps.hasUserLocation !== this.props.hasUserLocation){
    //         this.setState({
    //             lat: nextProps.lat,
    //             lng: nextProps.long,
    //             zoom: 15
    //         });
    //     }
    // }

    componentDidUpdate(prevProps) {
        if (this.props.hasUserLocation !== prevProps.hasUserLocation) {
            this.setState({
                lat: this.props.lat,
                lng: this.props.long,
                zoom: 15
            });
        }
    }



    addUserMarker(){
        const position = [this.state.lat, this.state.lng]
        const myIcon = L.icon({
            iconUrl: 'data:image/png;base64,',
            iconSize: [14, 45],
            iconAnchor: null,
            popupAnchor: [0, -15]
        });

        if(this.props.hasUserLocation){
            return(
                <Marker 
                    position={position}
                    icon={myIcon}
                >
                    <Popup>
                    You!
                    </Popup>
                </Marker>
            )
        }
    }

    findVenueLocations(){
        const auth_token = process.env.REACT_APP_EVENTBRITE_API_KEY;
        const venue_id = 31062470
        if(this.props.events.length >= 0){
        //     events.forEach((event)=>{
                fetch(`https://www.eventbriteapi.com/v3/venues/${venue_id}/?token=${auth_token}`)
                .then(response => response.json())
                .then(data => {
                    console.log(data);
                })
        //     })
        }
    }




    render() {
        const position = [this.state.lat, this.state.lng]
        return (
            <div className='map'>
                <Map className='leafMap' center={position} zoom={this.state.zoom}>
                    <TileLayer
                        attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    {this.addUserMarker()}
                </Map>
            </div>

        )
    }

}

export default MyMap;
